class Sourate {
  int numero;
  String nom;
  String nomarabe;
  String nbaya;
  List<Lecteur> lecteurs;
  List<Verset> versets;
  String audio;

  Sourate(
      {this.numero,
      this.nom,
      this.nbaya,
      this.lecteurs,
      this.audio,
      this.nomarabe,
      this.versets});

  List<Verset> getVersets() => this.versets;
  String getAudio() => this.audio;
  String getNom() => this.nom;
  String getNomarabe() => this.nomarabe;
  String getNbaya() => this.nbaya;
  int getNumero() => this.numero;
  List<Lecteur> getLecteurs() => this.lecteurs;
}

class Verset {
  String numero;
  int numv;
  String arabe;
  String wolof;
  String francais;
  String audio;
  Verset(
      {this.numv,
      this.numero,
      this.audio,
      this.arabe,
      this.francais,
      this.wolof});
  String getArabe() => this.arabe;
  String getWolof() => this.wolof;
  String getFranais() => this.francais;
  String getNumero() => this.numero;
  String getAudio() => this.audio;
  int getNumv() => this.numv;
}

class Sujod {
  Sourate sourate;
  int numverset;
  List<int> etoiles;
  Sujod({this.sourate, this.numverset, this.etoiles});
  Sourate getSourate() => this.sourate;
  int getNumverset() => this.numverset;
  List<int> getEtoiles() => this.etoiles;

  static bool withSujod(Sourate sourate, int numverset) {
    bool verify = false;
    //  sujods.forEach((sujod){
    for (Sujod sujod in sujods) {
      if ((sujod.getSourate() == sourate) &&
          (numverset == sujod.getNumverset())) {
        verify = true;
        break;
      }
    }
    return verify;
  }

  static bool withStart(Sourate sourate, int numeroverset) {
    bool verify = false;
    //  sujods.forEach((sujod){
    for (Sujod sujod in start) {
      if ((sujod.getSourate() == sourate)) {
        for (int numverset in sujod.getEtoiles()) {
          if (numverset == numeroverset) {
            verify = true;
            break;
          }
        }
        break;
      }
    }

    return verify;
  }
}

