      Verset(
          numv: 68,
          numero: '068',
          arabe:
              "أُبَلِّغُكُمْ رِسَالاتِ رَبِّي وَأَنَاْ لَكُمْ نَاصِحٌ أَمِينٌ ",
          francais:
