      Verset(
          numv: 114,
          numero: '114',
          arabe: "قَالَ نَعَمْ وَإَنَّكُمْ لَمِنَ الْمُقَرَّبِينَ ",
          francais: " Qāla Na`am Wa 'Innakum Lamina Al-Muqarrabīna",
          wolof: "Mu ne leen : “Waawaaw, kon dangeen bokk ci ñi may jegeñ”."),
      Verset(
          numv: 115,
          numero: '115',
          arabe:
              "قَالُواْ يَا مُوسَى إِمَّا أَن تُلْقِيَ وَإِمَّا أَن نَّكُونَ نَحْنُ الْمُلْقِينَ ",
          francais:
